        [TestMethod]
        public void ReaderQrCode()
        {
            // Arrange 
            HomeController controller = new HomeController();

            // Act
             string base64String = "";
             var result = controller.ReaderQrCode(base64String);
            dynamic x = JsonConvert.SerializeObject(result.Data);
            JObject data = JObject.Parse(x);
            BusinessCard business = new BusinessCard();

            // Assert
            business.Phone = "00962798888888";
            business.Name = "Bill Gates";
            business.Gender = "Male";
            business.DateOfBirth = "28/10/1955";
            business.Email = "Bill.Gates@microsoft.com";
            business.Phone = "00962798888888";
            business.Photo = null;
            business.Address= "USA";

            Assert.IsNotNull(data);
            Assert.AreEqual(business.Phone, data["Phone"] , business.Name, data["Name"], 
                business.Gender, data["Gender"] , business.DateOfBirth, data["DateOfBirth"] , business.Email, data["Email"]
                , business.Address, data["Address"] );


        }
        [TestMethod]
        
        public void InsertBusinessCard()
        {
