            string DateOfBirth = "test";
            string Email = "test";
            string Phone = "test";
            string Photo = "test";
            string Address = "test";
            var result = controller.InsertBusinessCard(Name, Gender, DateOfBirth,Email, Phone, Photo, Address);

            // Assert
            Assert.AreEqual("Done", result);
